-- db_intranet2.tbl_notescreen_article definition

CREATE TABLE `tbl_notescreen_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `schoolId` int(11) NOT NULL DEFAULT 0,
  `notescreenPageId` int(11) NOT NULL DEFAULT 0,
  `title` varchar(254) COLLATE utf8_unicode_ci NOT NULL,
  `content` blob DEFAULT NULL,
  `displayTime` int(11) NOT NULL DEFAULT 1000,
  `deleted` tinyint(1) NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- db_intranet2.tbl_notescreen_page definition

CREATE TABLE `tbl_notescreen_page` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `schoolId` int(11) NOT NULL DEFAULT 0,
  `name` varchar(128) COLLATE utf8_unicode_ci NOT NULL,
  `deleted` tinyint(1) NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

